import * as mobilenet from "@tensorflow-models/mobilenet";
import React, { useReducer, useRef, useState } from 'react';
import './App.css';

const stateMachine = {
  initial: { on: { next: 'loadingModel' } },
  loadingModel: { on: { next: 'awaitingUpload' } },
  awaitingUpload: { on: { next: 'imageReady' } },
  imageReady: { on: { next: 'classifying' } },
  classifying: { on: { next: 'complete' } },
  complete: { on: { next: 'awaitingUpload' } },
}

const machine = {
  initial: "initial",
  states: {
    initial: { on: { next: 'loadingModel' } },
    loadingModel: { on: { next: 'awaitingUpload' } },
    awaitingUpload: { on: { next: 'imageReady' } },
    imageReady: { on: { next: 'classifying', showImage: true } },
    classifying: { on: { next: 'complete' } },
    complete: { on: { next: 'awaitingUpload' }, showImage: true, showResult: true },
  }
}
function App() {
  const [results, setResults] = useState([]);
  const [imageUrl, setImageUrl] = useState(null);
  const [model, setModel] = useState(null);
  const imageRef = useRef();
  const inputRef = useRef();

  const reducer = (state, event) =>
    machine.states[state].on[event] || machine.initial;
  const [appState, dispatch] = useReducer(reducer, machine.initial);
  const next = () => dispatch("next");

  const loadModel = async () => {
    next();
    const model = await mobilenet.load();
    setModel(model);
    next();
  }
  return (
    <div className="App">
    </div>
  );
}

export default App;
